# for word problems, check if you can create an arrary of all letters and use that as hash instead of a dict
# if 
def checkInclusion(s1, s2):
    target = [0]*26
    for x in s1:
        target[ord(x) - ord('a')] += 1
    
    window = [0]*26

    for i in range(len(s2)):
        window[ord(s2[i]) - ord('a')] += 1
        if i >= len(s1):
            window[ord(s2[i-len(s1)]) - ord('a')] -= 1
        if window == target:
            return True
    return False
    





if __name__ == '__main__':
    print(checkInclusion("bba","eidoboaoabbboo"),True)
    print(checkInclusion("hello","ooolleoooleh"),False)
    print(checkInclusion("adc","dcda"),True)
    print(checkInclusion("cab","abcdeabcdfghij"),True)
    print(checkInclusion("",""))
