# Input: s = "abcabcbb"
# Output: 3
# Explanation: The answer is "abc", with the length of 3.

# Input: s = "bbbbb"
# Output: 1
# Explanation: The answer is "b", with the length of 1.

# Input: s = "pwwkew"
# Output: 3
# Explanation: The answer is "wke", with the length of 3.
# Notice that the answer must be a substring, "pwke" is a subsequence and not a substring.


from typing import Counter


def checkInclusion(s1, s2):
    target = [0]*26
    for x in s1:
        target[ord(x) - ord('a')] += 1
    
    window = [0]*26

    for i in range(len(s2)):
        window[ord(s2[i]) - ord('a')] += 1
        if i >= len(s1):
            window[ord(s2[i-len(s1)]) - ord('a')] -= 1
        if window == target:
            return True
    return False
    





if __name__ == '__main__':
    print(checkInclusion("bba","eidoboaoabbboo"),True)
    print(checkInclusion("hello","ooolleoooleh"),False)
    print(checkInclusion("adc","dcda"),True)
    print(checkInclusion("cab","abcdeabcdfghij"),True)
    print(checkInclusion("",""))
